,p_plsql_code=>wwv_flow_string.join(wwv_flow_t_varchar2(
'FUNCTION custom_login(',
'      p_username IN VARCHAR2,',
'      p_password IN VARCHAR2)',
'RETURN BOOLEAN IS',
'    ln_cnt  NUMBER;',
'BEGIN',
'    SELECT COUNT(1) ',
'    INTO   ln_cnt',
'    FROM   KMCHAT_KB_USER ',
'    WHERE  UPPER(USER_NAME) = UPPER(p_username) ',
'    AND    USER_PWD = p_password',
'    AND    app_id   = V(''APP_ID'');',
'',
'    IF ln_cnt > 0 THEN',
'        RETURN true;',
'    ELSE',
'        RETURN false;',
'    END IF;',
'EXCEPTION',
'  WHEN OTHERS THEN',
'    RETURN false;',
'END;'))
